import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { doc, getDoc } from 'firebase/firestore';
import { db } from '../firebase';
import { sampleProperties } from '../data/sampleProperties';
import { hosts } from '../data/hosts';
import ContactHostModal from './ContactHostModal';
import PaymentModal from './PaymentModal';
import './PropertyDetails.css';

const PropertyDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [property, setProperty] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [activeTab, setActiveTab] = useState('details');
  const [showContactModal, setShowContactModal] = useState(false);
  const [showPayment, setShowPayment] = useState(false);
  const [bookingDetails, setBookingDetails] = useState({
    checkIn: '',
    checkOut: '',
    guests: 1,
    totalNights: 0,
    totalAmount: 0
  });

  useEffect(() => {
    const fetchProperty = async () => {
      try {
        console.log('Fetching property with ID:', id);
        
        // First try to get from sample data
        const sampleProperty = sampleProperties.find(p => p.id === id);
        if (sampleProperty) {
          console.log('Property found in sample data:', sampleProperty);
          setProperty(sampleProperty);
          setLoading(false);
          return;
        }

        // If not in sample data, try Firebase
        try {
          const docRef = doc(db, 'properties', id);
          const docSnap = await getDoc(docRef);
          
          if (docSnap.exists()) {
            console.log('Property found in Firebase:', docSnap.data());
            setProperty({ id: docSnap.id, ...docSnap.data() });
          } else {
            console.log('Property not found in Firebase');
            setError('Property not found');
          }
        } catch (firebaseError) {
          console.error('Firebase error:', firebaseError);
          // If Firebase fails, use sample data as fallback
          if (sampleProperty) {
            console.log('Using sample data as fallback');
            setProperty(sampleProperty);
          } else {
            setError('Error loading property details');
          }
        }
      } catch (err) {
        console.error('Error in fetchProperty:', err);
        setError('Error loading property details');
      } finally {
        setLoading(false);
      }
    };

    fetchProperty();
  }, [id]);

  const nextImage = () => {
    if (!property?.images) return;
    setCurrentImageIndex((prev) => 
      prev === property.images.length - 1 ? 0 : prev + 1
    );
  };

  const prevImage = () => {
    if (!property?.images) return;
    setCurrentImageIndex((prev) => 
      prev === 0 ? property.images.length - 1 : prev - 1
    );
  };

  const formatPrice = (price) => {
    return new Intl.NumberFormat('en-UG', {
      style: 'currency',
      currency: 'UGX',
      maximumFractionDigits: 0
    }).format(price);
  };

  const handleContactClick = () => {
    setShowContactModal(true);
  };

  const handleDateChange = (e) => {
    const { name, value } = e.target;
    setBookingDetails(prev => {
      const newDetails = { ...prev, [name]: value };
      
      // Calculate total nights and amount if both dates are selected
      if (newDetails.checkIn && newDetails.checkOut) {
        const checkIn = new Date(newDetails.checkIn);
        const checkOut = new Date(newDetails.checkOut);
        const nights = Math.ceil((checkOut - checkIn) / (1000 * 60 * 60 * 24));
        const totalAmount = nights * property.price;
        
        return {
          ...newDetails,
          totalNights: nights,
          totalAmount: totalAmount
        };
      }
      
      return newDetails;
    });
  };

  const handleGuestChange = (e) => {
    const guests = parseInt(e.target.value);
    setBookingDetails(prev => ({
      ...prev,
      guests,
      totalAmount: prev.totalNights * property.price
    }));
  };

  const handleBookingSubmit = (e) => {
    e.preventDefault();
    if (bookingDetails.checkIn && bookingDetails.checkOut) {
      setShowPayment(true);
    }
  };

  const handlePaymentSuccess = (result) => {
    // Here you would typically save the booking to your database
    console.log('Payment successful:', result);
    alert('Booking confirmed! Thank you for your reservation.');
    navigate('/');
  };

  if (loading) {
    return (
      <div className="loading">
        <div className="loading-spinner"></div>
        <p>Loading property details...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error">
        <p>{error}</p>
        <button onClick={() => navigate('/')} className="back-button">
          Return to Listings
        </button>
      </div>
    );
  }

  if (!property) {
    return (
      <div className="error">
        <p>Property not found</p>
        <button onClick={() => navigate('/')} className="back-button">
          Return to Listings
        </button>
      </div>
    );
  }

  const host = hosts[property.hostId];

  return (
    <div className="property-details-container">
      <button onClick={() => navigate('/')} className="back-button">
        ← Back to Listings
      </button>

      <div className="property-header">
        <h1>{property.title}</h1>
        <p className="location">{property.location}</p>
      </div>

      {property.images && property.images.length > 0 && (
        <div className="image-gallery">
          <div className="main-image-container">
            <img 
              src={property.images[currentImageIndex]} 
              alt={property.title}
              className="main-image"
              onError={(e) => {
                e.target.onerror = null;
                e.target.src = 'https://via.placeholder.com/800x500?text=Image+Not+Available';
              }}
            />
            <button className="nav-button prev" onClick={prevImage}>←</button>
            <button className="nav-button next" onClick={nextImage}>→</button>
          </div>
          <div className="thumbnail-container">
            {property.images.map((image, index) => (
              <img
                key={index}
                src={image}
                alt={`${property.title} - Image ${index + 1}`}
                className={`thumbnail ${index === currentImageIndex ? 'active' : ''}`}
                onClick={() => setCurrentImageIndex(index)}
                onError={(e) => {
                  e.target.onerror = null;
                  e.target.src = 'https://via.placeholder.com/100x70?text=Image+Not+Available';
                }}
              />
            ))}
          </div>
        </div>
      )}

      <div className="property-tabs">
        <button 
          className={`tab-button ${activeTab === 'details' ? 'active' : ''}`}
          onClick={() => setActiveTab('details')}
        >
          Details
        </button>
        <button 
          className={`tab-button ${activeTab === 'host' ? 'active' : ''}`}
          onClick={() => setActiveTab('host')}
        >
          Host
        </button>
        <button 
          className={`tab-button ${activeTab === 'location' ? 'active' : ''}`}
          onClick={() => setActiveTab('location')}
        >
          Location
        </button>
      </div>

      <div className="property-content">
        {activeTab === 'details' && (
          <div className="details-section">
            <div className="property-info-grid">
              <div className="info-item">
                <h3>Bedrooms</h3>
                <p>{property.bedrooms}</p>
              </div>
              <div className="info-item">
                <h3>Bathrooms</h3>
                <p>{property.bathrooms}</p>
              </div>
              <div className="info-item">
                <h3>Max Guests</h3>
                <p>{property.maxGuests}</p>
              </div>
              <div className="info-item">
                <h3>Size</h3>
                <p>{property.size}</p>
              </div>
            </div>

            <div className="description-section">
              <h2>About this place</h2>
              <p>{property.description}</p>
            </div>

            {property.amenities && (
              <div className="amenities-section">
                <h2>Amenities</h2>
                <div className="amenities-grid">
                  {property.amenities.map((amenity, index) => (
                    <div key={index} className="amenity-item">
                      <span>✓</span> {amenity}
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
        )}

        {activeTab === 'host' && (
          <div className="host-section">
            <div className="host-profile">
              {host && (
                <>
                  <img 
                    src={host.image} 
                    alt={host.name}
                    className="host-image"
                    onError={(e) => {
                      e.target.onerror = null;
                      e.target.src = 'https://via.placeholder.com/150x150?text=Host+Image';
                    }}
                  />
                  <div className="host-info">
                    <h2>Meet your host</h2>
                    <h3>{host.name}</h3>
                    <p className="host-status">{host.status}</p>
                    <p>Member since {host.memberSince}</p>
                    <div className="host-stats">
                      <div className="stat">
                        <span>{host.rating}</span>
                        <p>Rating</p>
                      </div>
                      <div className="stat">
                        <span>{host.reviews}+</span>
                        <p>Reviews</p>
                      </div>
                      <div className="stat">
                        <span>{host.responseRate}%</span>
                        <p>Response rate</p>
                      </div>
                    </div>
                    <div className="host-details">
                      <p className="response-time">
                        <span>Response time:</span> {host.responseTime}
                      </p>
                      <p className="languages">
                        <span>Languages:</span> {host.languages.join(', ')}
                      </p>
                      {host.verified && (
                        <p className="verified">
                          <span>✓</span> Identity verified
                        </p>
                      )}
                    </div>
                    <p className="host-description">
                      {host.description}
                    </p>
                    <button className="contact-button">Contact Host</button>
                  </div>
                </>
              )}
            </div>
          </div>
        )}

        {activeTab === 'location' && (
          <div className="location-section">
            <h2>Location</h2>
            <div className="map-placeholder">
              <p>Map will be displayed here</p>
            </div>
            <div className="location-details">
              <h3>Getting around</h3>
              <ul>
                <li>5 min walk to public transport</li>
                <li>10 min drive to city center</li>
                <li>15 min walk to nearest restaurant</li>
              </ul>
              <h3>Nearby attractions</h3>
              <ul>
                <li>Local market - 5 min walk</li>
                <li>Park - 10 min walk</li>
                <li>Shopping mall - 15 min drive</li>
              </ul>
            </div>
          </div>
        )}
      </div>

      <div className="price-card">
        <div className="price-header">
          <h3>{formatPrice(property.price)}</h3>
          <span>per night</span>
        </div>
        <div className="price-details">
          <div className="price-row">
            <span>{formatPrice(property.price)} x 1 night</span>
            <span>{formatPrice(property.price)}</span>
          </div>
          <div className="price-row">
            <span>Service fee</span>
            <span>{formatPrice(property.price * 0.12)}</span>
          </div>
          <div className="price-row total">
            <span>Total</span>
            <span>{formatPrice(property.price + (property.price * 0.12))}</span>
          </div>
        </div>
        <button className="contact-button" onClick={handleContactClick}>
          Contact Host
        </button>
      </div>

      {showContactModal && host && (
        <ContactHostModal
          host={host}
          onClose={() => setShowContactModal(false)}
        />
      )}

      <div className="booking-card">
        <div className="price-section">
          <h2>UGX {property.price.toLocaleString()}</h2>
          <span>per night</span>
        </div>

        <form onSubmit={handleBookingSubmit} className="booking-form">
          <div className="date-inputs">
            <div className="input-group">
              <label>Check-in</label>
              <input
                type="date"
                name="checkIn"
                value={bookingDetails.checkIn}
                onChange={handleDateChange}
                min={new Date().toISOString().split('T')[0]}
                required
              />
            </div>
            <div className="input-group">
              <label>Check-out</label>
              <input
                type="date"
                name="checkOut"
                value={bookingDetails.checkOut}
                onChange={handleDateChange}
                min={bookingDetails.checkIn || new Date().toISOString().split('T')[0]}
                required
              />
            </div>
          </div>

          <div className="input-group">
            <label>Guests</label>
            <select
              name="guests"
              value={bookingDetails.guests}
              onChange={handleGuestChange}
              required
            >
              {[...Array(property.maxGuests)].map((_, i) => (
                <option key={i + 1} value={i + 1}>
                  {i + 1} {i === 0 ? 'guest' : 'guests'}
                </option>
              ))}
            </select>
          </div>

          {bookingDetails.totalNights > 0 && (
            <div className="booking-summary">
              <div className="summary-row">
                <span>UGX {property.price.toLocaleString()} x {bookingDetails.totalNights} nights</span>
                <span>UGX {(property.price * bookingDetails.totalNights).toLocaleString()}</span>
              </div>
              <div className="summary-row">
                <span>Service fee</span>
                <span>UGX {(property.price * bookingDetails.totalNights * 0.1).toLocaleString()}</span>
              </div>
              <div className="summary-row total">
                <span>Total</span>
                <span>UGX {bookingDetails.totalAmount.toLocaleString()}</span>
              </div>
            </div>
          )}

          <button
            type="submit"
            className="book-button"
            disabled={!bookingDetails.checkIn || !bookingDetails.checkOut}
          >
            Book Now
          </button>
        </form>
      </div>

      {showPayment && (
        <PaymentModal
          amount={bookingDetails.totalAmount}
          onSuccess={handlePaymentSuccess}
          onClose={() => setShowPayment(false)}
        />
      )}
    </div>
  );
};

export default PropertyDetails;
