import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPhone, faEnvelope } from '@fortawesome/free-solid-svg-icons';
import { faWhatsapp } from '@fortawesome/free-brands-svg-icons';
import './ContactHostModal.css';

const ContactHostModal = ({ isOpen, onClose, host }) => {
  if (!isOpen) return null;

  const handleWhatsAppClick = () => {
    const message = `Hello, I'm interested in your property.`;
    const whatsappUrl = `https://wa.me/${host.phone}?text=${encodeURIComponent(message)}`;
    window.open(whatsappUrl, '_blank');
  };

  const handleSMSClick = () => {
    const message = `Hello, I'm interested in your property.`;
    const smsUrl = `sms:${host.phone}?body=${encodeURIComponent(message)}`;
    window.location.href = smsUrl;
  };

  const handleCallClick = () => {
    const callUrl = `tel:${host.phone}`;
    window.location.href = callUrl;
  };

  const handleEmailClick = () => {
    const subject = 'Inquiry about your property';
    const body = 'Hello, I am interested in your property.';
    const mailtoUrl = `mailto:${host.email}?subject=${encodeURIComponent(subject)}&body=${encodeURIComponent(body)}`;
    window.location.href = mailtoUrl;
  };

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={e => e.stopPropagation()}>
        <button className="close-button" onClick={onClose}>Ã—</button>
        <h2>Contact Host</h2>
        <div className="contact-info">
          <p>Name: {host.name}</p>
          <p>Phone: {host.phone}</p>
          <p>Email: {host.email}</p>
        </div>
        <div className="contact-buttons-grid">
          <button className="whatsapp-button" onClick={handleWhatsAppClick}>
            <FontAwesomeIcon icon={faWhatsapp} /> WhatsApp
          </button>
          <button className="sms-button" onClick={handleSMSClick}>
            <FontAwesomeIcon icon={faEnvelope} /> SMS
          </button>
          <button className="call-button" onClick={handleCallClick}>
            <FontAwesomeIcon icon={faPhone} /> Call
          </button>
          <button className="email-button" onClick={handleEmailClick}>
            <FontAwesomeIcon icon={faEnvelope} /> Email
          </button>
        </div>
      </div>
    </div>
  );
};

export default ContactHostModal;
