import React, { useState } from 'react';
import { CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import './PaymentModal.css';

const PaymentModal = ({ amount, onClose, onSuccess }) => {
  const stripe = useStripe();
  const elements = useElements();
  const [error, setError] = useState(null);
  const [processing, setProcessing] = useState(false);
  const [paymentMethod, setPaymentMethod] = useState('card');

  const handleSubmit = async (event) => {
    event.preventDefault();
    
    if (!stripe || !elements) {
      return;
    }

    setProcessing(true);
    setError(null);

    try {
      if (paymentMethod === 'card') {
        const { error: stripeError, paymentMethod: stripePaymentMethod } = await stripe.createPaymentMethod({
          type: 'card',
          card: elements.getElement(CardElement),
        });

        if (stripeError) {
          setError(stripeError.message);
          return;
        }

        // Here you would typically send the payment method ID to your server
        console.log('Payment Method:', stripePaymentMethod);
        onSuccess(stripePaymentMethod);
      } else if (paymentMethod === 'mtn') {
        // Handle MTN Mobile Money payment
        console.log('Processing MTN Mobile Money payment');
        // Implement MTN payment logic
      } else if (paymentMethod === 'airtel') {
        // Handle Airtel Money payment
        console.log('Processing Airtel Money payment');
        // Implement Airtel payment logic
      }
    } catch (err) {
      setError('An unexpected error occurred.');
      console.error('Payment error:', err);
    } finally {
      setProcessing(false);
    }
  };

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={e => e.stopPropagation()}>
        <button className="close-button" onClick={onClose}>×</button>
        
        <h2>Complete Your Payment</h2>
        <p className="amount">Amount: UGX {amount.toLocaleString()}</p>

        <div className="payment-methods">
          <button
            className={`payment-method ${paymentMethod === 'card' ? 'selected' : ''}`}
            onClick={() => setPaymentMethod('card')}
          >
            Credit Card
          </button>
          <button
            className={`payment-method ${paymentMethod === 'mtn' ? 'selected' : ''}`}
            onClick={() => setPaymentMethod('mtn')}
          >
            MTN Mobile Money
          </button>
          <button
            className={`payment-method ${paymentMethod === 'airtel' ? 'selected' : ''}`}
            onClick={() => setPaymentMethod('airtel')}
          >
            Airtel Money
          </button>
        </div>

        <form onSubmit={handleSubmit}>
          {paymentMethod === 'card' && (
            <div className="card-element-container">
              <CardElement
                options={{
                  style: {
                    base: {
                      fontSize: '16px',
                      color: '#424770',
                      '::placeholder': {
                        color: '#aab7c4',
                      },
                    },
                    invalid: {
                      color: '#9e2146',
                    },
                  },
                }}
              />
            </div>
          )}

          {paymentMethod === 'mtn' && (
            <div className="mobile-money-input">
              <input
                type="tel"
                placeholder="Enter MTN Mobile Money number"
                pattern="[0-9]{10}"
                required
              />
            </div>
          )}

          {paymentMethod === 'airtel' && (
            <div className="mobile-money-input">
              <input
                type="tel"
                placeholder="Enter Airtel Money number"
                pattern="[0-9]{10}"
                required
              />
            </div>
          )}

          {error && <div className="error-message">{error}</div>}

          <button
            type="submit"
            className="pay-button"
            disabled={!stripe || processing}
          >
            {processing ? 'Processing...' : 'Pay Now'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default PaymentModal;
