import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { doc, getDoc } from 'firebase/firestore';
import { db } from '../firebase';
import { sampleProperties } from '../data/sampleProperties';
import './PropertyDetails.css';

const PropertyDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [property, setProperty] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  useEffect(() => {
    const fetchProperty = async () => {
      try {
        console.log('Fetching property with ID:', id);
        const docRef = doc(db, 'properties', id);
        const docSnap = await getDoc(docRef);
        
        if (docSnap.exists()) {
          console.log('Property found in Firebase:', docSnap.data());
          setProperty({ id: docSnap.id, ...docSnap.data() });
        } else {
          console.log('Property not found in Firebase, checking sample data');
          // If not in Firebase, check sample data
          const sampleProperty = sampleProperties.find(p => p.id === id);
          if (sampleProperty) {
            console.log('Property found in sample data:', sampleProperty);
            setProperty(sampleProperty);
          } else {
            console.log('Property not found in sample data either');
            setError('Property not found');
          }
        }
      } catch (err) {
        console.error('Error fetching property:', err);
        setError('Error fetching property details: ' + err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchProperty();
  }, [id]);

  const handleImageClick = (index) => {
    setCurrentImageIndex(index);
  };

  if (loading) {
    return (
      <div className="loading">
        <div className="loading-spinner"></div>
        Loading property details...
      </div>
    );
  }

  if (error) {
    return (
      <div className="error">
        <p>{error}</p>
        <button onClick={() => navigate('/')} className="back-button">
          Return to Listings
        </button>
      </div>
    );
  }

  if (!property) {
    return (
      <div className="error">
        <p>Property not found</p>
        <button onClick={() => navigate('/')} className="back-button">
          Return to Listings
        </button>
      </div>
    );
  }

  return (
    <div className="property-details-container">
      <button className="back-button" onClick={() => navigate(-1)}>
        ← Back to Listings
      </button>

      <div className="property-header">
        <h1>{property.title}</h1>
        <p className="location">
          <i className="fas fa-map-marker-alt"></i>
          {property.location}
        </p>
      </div>

      <div className="image-gallery">
        <div className="main-image">
          <img 
            src={property.images[currentImageIndex]} 
            alt={property.title}
            onError={(e) => {
              e.target.onerror = null;
              e.target.src = 'https://via.placeholder.com/800x500?text=Image+Not+Available';
            }}
          />
        </div>
        <div className="thumbnail-container">
          {property.images.map((image, index) => (
            <div 
              key={index}
              className={`thumbnail ${index === currentImageIndex ? 'active' : ''}`}
              onClick={() => handleImageClick(index)}
            >
              <img 
                src={image} 
                alt={`${property.title} - Image ${index + 1}`}
                onError={(e) => {
                  e.target.onerror = null;
                  e.target.src = 'https://via.placeholder.com/100x70?text=Image+Not+Available';
                }}
              />
            </div>
          ))}
        </div>
      </div>

      <div className="property-info-grid">
        <div className="main-info">
          <div className="property-description">
            <h2>About this place</h2>
            <p>{property.description}</p>
          </div>

          <div className="property-features">
            <h2>Features</h2>
            <div className="features-grid">
              <div className="feature">
                <i className="fas fa-bed"></i>
                <span>{property.bedrooms} Bedrooms</span>
              </div>
              <div className="feature">
                <i className="fas fa-bath"></i>
                <span>{property.bathrooms} Bathrooms</span>
              </div>
              <div className="feature">
                <i className="fas fa-users"></i>
                <span>Up to {property.maxGuests} guests</span>
              </div>
              <div className="feature">
                <i className="fas fa-ruler-combined"></i>
                <span>{property.size}</span>
              </div>
            </div>
          </div>

          <div className="amenities">
            <h2>Amenities</h2>
            <div className="amenities-grid">
              {property.amenities.map((amenity, index) => (
                <div key={index} className="amenity">
                  <i className="fas fa-check"></i>
                  <span>{amenity}</span>
                </div>
              ))}
            </div>
          </div>
        </div>

        <div className="booking-card">
          <div className="price">
            <span className="amount">${property.price}</span>
            <span className="period">per night</span>
          </div>
          <button className="contact-button">
            Contact Host
          </button>
        </div>
      </div>
    </div>
  );
};

export default PropertyDetails;
