import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { collection, getDocs } from 'firebase/firestore';
import { db } from '../firebase';
import './Listings.css';
import ImageGallery from 'react-image-gallery';
import 'react-image-gallery/styles/css/image-gallery.css';
import { GoogleMap, Marker, InfoWindow, useJsApiLoader } from '@react-google-maps/api';

const MAPS_API_KEY = 'YOUR_GOOGLE_MAPS_API_KEY'; // Replace with your real key
const MAP_CONTAINER_STYLE = { width: '100%', height: '400px' };
const MAP_CENTER = { lat: 1.3733, lng: 32.2903 }; // Center of Uganda

const Listings = () => {
  const navigate = useNavigate();
  const [selectedArea, setSelectedArea] = useState('all');
  const [priceRange, setPriceRange] = useState('all');
  const [amenities, setAmenities] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [sortBy, setSortBy] = useState('default');
  const [showMap, setShowMap] = useState(false);
  const [activeMarker, setActiveMarker] = useState(null);
  const [properties, setProperties] = useState([]);
  const [filteredProperties, setFilteredProperties] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [filters, setFilters] = useState({
    location: '',
    priceRange: '',
    propertyType: '',
    amenities: []
  });

  const { isLoaded } = useJsApiLoader({
    googleMapsApiKey: MAPS_API_KEY,
  });

  useEffect(() => {
    const fetchProperties = async () => {
      try {
        const querySnapshot = await getDocs(collection(db, 'properties'));
        const propertiesList = querySnapshot.docs.map(doc => ({
          id: doc.id,
          ...doc.data()
        }));
        setProperties(propertiesList);
        setFilteredProperties(propertiesList);
      } catch (err) {
        setError('Error fetching properties');
        console.error('Error:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchProperties();
  }, []);

  useEffect(() => {
    let filtered = [...properties];

    if (filters.location) {
      filtered = filtered.filter(property => 
        property.location.toLowerCase().includes(filters.location.toLowerCase())
      );
    }

    if (filters.priceRange) {
      const [min, max] = filters.priceRange.split('-').map(Number);
      filtered = filtered.filter(property => {
        const price = property.price;
        if (max) {
          return price >= min && price <= max;
        }
        return price >= min;
      });
    }

    if (filters.propertyType) {
      filtered = filtered.filter(property => 
        property.type.toLowerCase() === filters.propertyType.toLowerCase()
      );
    }

    setFilteredProperties(filtered);
  }, [filters, properties]);

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handlePropertyClick = (propertyId) => {
    navigate(`/property/${propertyId}`);
  };

  const areas = ['all', 'Kampala', 'Entebbe', 'Jinja', 'Mbarara', 'Masaka', 'Hoima', 'Fort Portal'];
  const priceRanges = [
    { value: 'all', label: 'All Prices' },
    { value: 'budget', label: 'Under $70', min: 0, max: 70 },
    { value: 'moderate', label: '$70 - $100', min: 70, max: 100 },
    { value: 'luxury', label: 'Over $100', min: 100, max: Infinity }
  ];
  const availableAmenities = [
    { id: 'wifi', label: 'WiFi' },
    { id: 'pool', label: 'Pool' },
    { id: 'parking', label: 'Parking' },
    { id: 'aircon', label: 'Air Conditioning' },
    { id: 'beach', label: 'Beach Access' },
    { id: 'mountain-view', label: 'Mountain View' },
    { id: 'river-view', label: 'River View' },
    { id: 'lake-view', label: 'Lake View' },
    { id: 'garden', label: 'Garden' },
    { id: 'restaurant', label: 'Restaurant' }
  ];

  const sortOptions = [
    { value: 'default', label: 'Recommended' },
    { value: 'price-asc', label: 'Price: Low to High' },
    { value: 'price-desc', label: 'Price: High to Low' },
    { value: 'rating', label: 'Highest Rated' },
    { value: 'reviews', label: 'Most Reviewed' }
  ];

  const handleAmenityChange = (amenityId) => {
    setAmenities(prev => 
      prev.includes(amenityId)
        ? prev.filter(id => id !== amenityId)
        : [...prev, amenityId]
    );
  };

  const filteredApartments = filteredProperties
    .filter(property => {
      // Area filter
      const areaMatch = selectedArea === 'all' || property.location === selectedArea;
      
      // Price range filter
      const priceRangeObj = priceRanges.find(range => range.value === priceRange);
      const priceMatch = priceRange === 'all' || 
        (property.price >= priceRangeObj.min && property.price <= priceRangeObj.max);
      
      // Amenities filter
      const amenitiesMatch = amenities.length === 0 || 
        amenities.every(amenity => property.amenities.includes(amenity));

      // Search filter
      const searchMatch = searchQuery === '' || 
        property.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
        property.location.toLowerCase().includes(searchQuery.toLowerCase()) ||
        property.description.toLowerCase().includes(searchQuery.toLowerCase());

      return areaMatch && priceMatch && amenitiesMatch && searchMatch;
    })
    .sort((a, b) => {
      switch (sortBy) {
        case 'price-asc':
          return a.price - b.price;
        case 'price-desc':
          return b.price - a.price;
        case 'rating':
          return b.rating - a.rating;
        case 'reviews':
          return b.reviews - a.reviews;
        default:
          return 0;
      }
    });

  if (loading) {
    return <div className="loading">Loading properties...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="listings-container">
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search by title, location, or description..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          className="search-input"
        />
        <button 
          className="map-toggle"
          onClick={() => setShowMap(!showMap)}
        >
          {showMap ? 'Hide Map' : 'Show Map'}
        </button>
      </div>

      <div className="filters">
        <div className="filter-section">
          <h2>Filter by Area</h2>
          <select 
            value={selectedArea} 
            onChange={(e) => setSelectedArea(e.target.value)}
            className="area-select"
          >
            {areas.map(area => (
              <option key={area} value={area}>
                {area.charAt(0).toUpperCase() + area.slice(1)}
              </option>
            ))}
          </select>
        </div>

        <div className="filter-section">
          <h2>Price Range</h2>
          <select 
            value={priceRange} 
            onChange={(e) => setPriceRange(e.target.value)}
            className="price-select"
          >
            {priceRanges.map(range => (
              <option key={range.value} value={range.value}>
                {range.label}
              </option>
            ))}
          </select>
        </div>

        <div className="filter-section">
          <h2>Sort By</h2>
          <select 
            value={sortBy} 
            onChange={(e) => setSortBy(e.target.value)}
            className="sort-select"
          >
            {sortOptions.map(option => (
              <option key={option.value} value={option.value}>
                {option.label}
              </option>
            ))}
          </select>
        </div>

        <div className="filter-section">
          <h2>Amenities</h2>
          <div className="amenities-grid">
            {availableAmenities.map(amenity => (
              <label key={amenity.id} className="amenity-checkbox">
                <input
                  type="checkbox"
                  checked={amenities.includes(amenity.id)}
                  onChange={() => handleAmenityChange(amenity.id)}
                />
                {amenity.label}
              </label>
            ))}
          </div>
        </div>
      </div>

      {showMap && isLoaded && (
        <div className="map-container">
          <GoogleMap
            mapContainerStyle={MAP_CONTAINER_STYLE}
            center={MAP_CENTER}
            zoom={7}
          >
            {filteredProperties.map(property => (
              <Marker
                key={property.id}
                position={property.coordinates}
                onClick={() => setActiveMarker(property.id)}
              />
            ))}
            {filteredProperties.map(property => (
              activeMarker === property.id && (
                <InfoWindow
                  key={property.id}
                  position={property.coordinates}
                  onCloseClick={() => setActiveMarker(null)}
                >
                  <div style={{ maxWidth: 200 }}>
                    <img src={property.images[0]} alt={property.title} style={{ width: '100%', borderRadius: 8 }} />
                    <h4 style={{ margin: '8px 0 0 0' }}>{property.title}</h4>
                    <p style={{ margin: 0 }}>{property.location}</p>
                  </div>
                </InfoWindow>
              )
            ))}
          </GoogleMap>
        </div>
      )}

      <div className="properties-grid">
        {filteredApartments.map(property => (
          <div 
            key={property.id} 
            className="property-card"
            onClick={() => handlePropertyClick(property.id)}
          >
            <div className="property-image">
              <img src={property.images[0]} alt={property.title} />
            </div>
            <div className="property-info">
              <h3>{property.title}</h3>
              <p className="location">
                <i className="fas fa-map-marker-alt"></i>
                {property.location}
              </p>
              <div className="property-details">
                <span><i className="fas fa-bed"></i> {property.bedrooms} beds</span>
                <span><i className="fas fa-bath"></i> {property.bathrooms} baths</span>
                <span><i className="fas fa-users"></i> {property.maxGuests} guests</span>
              </div>
              <p className="price">${property.price} <span>per night</span></p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Listings;
