import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { doc, getDoc } from 'firebase/firestore';
import { db } from '../firebase';
import { sampleProperties } from '../data/sampleProperties';
import './PropertyDetails.css';

const PropertyDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [property, setProperty] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [activeTab, setActiveTab] = useState('details');

  useEffect(() => {
    const fetchProperty = async () => {
      try {
        console.log('Fetching property with ID:', id);
        
        // First try to get from sample data
        const sampleProperty = sampleProperties.find(p => p.id === id);
        if (sampleProperty) {
          console.log('Property found in sample data:', sampleProperty);
          setProperty(sampleProperty);
          setLoading(false);
          return;
        }

        // If not in sample data, try Firebase
        try {
          const docRef = doc(db, 'properties', id);
          const docSnap = await getDoc(docRef);
          
          if (docSnap.exists()) {
            console.log('Property found in Firebase:', docSnap.data());
            setProperty({ id: docSnap.id, ...docSnap.data() });
          } else {
            console.log('Property not found in Firebase');
            setError('Property not found');
          }
        } catch (firebaseError) {
          console.error('Firebase error:', firebaseError);
          // If Firebase fails, use sample data as fallback
          if (sampleProperty) {
            console.log('Using sample data as fallback');
            setProperty(sampleProperty);
          } else {
            setError('Error loading property details');
          }
        }
      } catch (err) {
        console.error('Error in fetchProperty:', err);
        setError('Error loading property details');
      } finally {
        setLoading(false);
      }
    };

    fetchProperty();
  }, [id]);

  const nextImage = () => {
    if (!property?.images) return;
    setCurrentImageIndex((prev) => 
      prev === property.images.length - 1 ? 0 : prev + 1
    );
  };

  const prevImage = () => {
    if (!property?.images) return;
    setCurrentImageIndex((prev) => 
      prev === 0 ? property.images.length - 1 : prev - 1
    );
  };

  if (loading) {
    return (
      <div className="loading">
        <div className="loading-spinner"></div>
        <p>Loading property details...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error">
        <p>{error}</p>
        <button onClick={() => navigate('/')} className="back-button">
          Return to Listings
        </button>
      </div>
    );
  }

  if (!property) {
    return (
      <div className="error">
        <p>Property not found</p>
        <button onClick={() => navigate('/')} className="back-button">
          Return to Listings
        </button>
      </div>
    );
  }

  return (
    <div className="property-details-container">
      <button onClick={() => navigate('/')} className="back-button">
        ← Back to Listings
      </button>

      <div className="property-header">
        <h1>{property.title}</h1>
        <p className="location">{property.location}</p>
      </div>

      {property.images && property.images.length > 0 && (
        <div className="image-gallery">
          <div className="main-image-container">
            <img 
              src={property.images[currentImageIndex]} 
              alt={property.title}
              className="main-image"
              onError={(e) => {
                e.target.onerror = null;
                e.target.src = 'https://via.placeholder.com/800x500?text=Image+Not+Available';
              }}
            />
            <button className="nav-button prev" onClick={prevImage}>←</button>
            <button className="nav-button next" onClick={nextImage}>→</button>
          </div>
          <div className="thumbnail-container">
            {property.images.map((image, index) => (
              <img
                key={index}
                src={image}
                alt={`${property.title} - Image ${index + 1}`}
                className={`thumbnail ${index === currentImageIndex ? 'active' : ''}`}
                onClick={() => setCurrentImageIndex(index)}
                onError={(e) => {
                  e.target.onerror = null;
                  e.target.src = 'https://via.placeholder.com/100x70?text=Image+Not+Available';
                }}
              />
            ))}
          </div>
        </div>
      )}

      <div className="property-tabs">
        <button 
          className={`tab-button ${activeTab === 'details' ? 'active' : ''}`}
          onClick={() => setActiveTab('details')}
        >
          Details
        </button>
        <button 
          className={`tab-button ${activeTab === 'host' ? 'active' : ''}`}
          onClick={() => setActiveTab('host')}
        >
          Host
        </button>
        <button 
          className={`tab-button ${activeTab === 'location' ? 'active' : ''}`}
          onClick={() => setActiveTab('location')}
        >
          Location
        </button>
      </div>

      <div className="property-content">
        {activeTab === 'details' && (
          <div className="details-section">
            <div className="property-info-grid">
              <div className="info-item">
                <h3>Bedrooms</h3>
                <p>{property.bedrooms}</p>
              </div>
              <div className="info-item">
                <h3>Bathrooms</h3>
                <p>{property.bathrooms}</p>
              </div>
              <div className="info-item">
                <h3>Max Guests</h3>
                <p>{property.maxGuests}</p>
              </div>
              <div className="info-item">
                <h3>Size</h3>
                <p>{property.size}</p>
              </div>
            </div>

            <div className="description-section">
              <h2>About this place</h2>
              <p>{property.description}</p>
            </div>

            {property.amenities && (
              <div className="amenities-section">
                <h2>Amenities</h2>
                <div className="amenities-grid">
                  {property.amenities.map((amenity, index) => (
                    <div key={index} className="amenity-item">
                      <span>✓</span> {amenity}
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
        )}

        {activeTab === 'host' && (
          <div className="host-section">
            <div className="host-profile">
              <img 
                src="https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80" 
                alt="Host"
                className="host-image"
                onError={(e) => {
                  e.target.onerror = null;
                  e.target.src = 'https://via.placeholder.com/150x150?text=Host+Image';
                }}
              />
              <div className="host-info">
                <h2>Meet your host</h2>
                <h3>John Doe</h3>
                <p>Superhost</p>
                <p>Member since 2020</p>
                <div className="host-stats">
                  <div className="stat">
                    <span>4.9</span>
                    <p>Rating</p>
                  </div>
                  <div className="stat">
                    <span>50+</span>
                    <p>Reviews</p>
                  </div>
                  <div className="stat">
                    <span>100%</span>
                    <p>Response rate</p>
                  </div>
                </div>
                <p className="host-description">
                  Experienced host with a passion for providing exceptional stays. 
                  I love sharing local insights and ensuring guests have a memorable experience.
                </p>
                <button className="contact-button">Contact Host</button>
              </div>
            </div>
          </div>
        )}

        {activeTab === 'location' && (
          <div className="location-section">
            <h2>Location</h2>
            <div className="map-placeholder">
              <p>Map will be displayed here</p>
            </div>
            <div className="location-details">
              <h3>Getting around</h3>
              <ul>
                <li>5 min walk to public transport</li>
                <li>10 min drive to city center</li>
                <li>15 min walk to nearest restaurant</li>
              </ul>
              <h3>Nearby attractions</h3>
              <ul>
                <li>Local market - 5 min walk</li>
                <li>Park - 10 min walk</li>
                <li>Shopping mall - 15 min drive</li>
              </ul>
            </div>
          </div>
        )}
      </div>

      <div className="booking-card">
        <div className="price-section">
          <h2>${property.price}</h2>
          <p>per night</p>
        </div>
        <button className="contact-button">Contact Host</button>
      </div>
    </div>
  );
};

export default PropertyDetails;
