// Payment Configuration
export const PAYMENT_CONFIG = {
  // Stripe Configuration
  stripe: {
    publicKey: 'your_stripe_public_key',
    secretKey: 'your_stripe_secret_key',
    currency: 'UGX',
  },
  
  // Mobile Money Configuration
  mobileMoney: {
    mtn: {
      apiKey: 'your_mtn_api_key',
      merchantId: 'your_mtn_merchant_id',
      callbackUrl: 'https://your-domain.com/api/payments/mtn/callback',
    },
    airtel: {
      apiKey: 'your_airtel_api_key',
      merchantId: 'your_airtel_merchant_id',
      callbackUrl: 'https://your-domain.com/api/payments/airtel/callback',
    },
  },
  
  // Payment Methods
  methods: {
    creditCard: {
      enabled: true,
      name: 'Credit Card',
      icon: 'credit-card',
    },
    mtn: {
      enabled: true,
      name: 'MTN Mobile Money',
      icon: 'mobile',
    },
    airtel: {
      enabled: true,
      name: 'Airtel Money',
      icon: 'mobile',
    },
  },
};
