import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { auth, db } from '../firebase';
import { doc, getDoc, collection, query, where, getDocs } from 'firebase/firestore';
import './Profile.css';

const Profile = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('profile');

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const currentUser = auth.currentUser;
        if (!currentUser) {
          navigate('/');
          return;
        }

        // Fetch user profile data
        const userDoc = await getDoc(doc(db, 'users', currentUser.uid));
        if (userDoc.exists()) {
          setUser({ id: userDoc.id, ...userDoc.data() });
        }

        // Fetch user's bookings
        const bookingsQuery = query(
          collection(db, 'bookings'),
          where('userId', '==', currentUser.uid)
        );
        const bookingsSnapshot = await getDocs(bookingsQuery);
        const bookingsList = await Promise.all(
          bookingsSnapshot.docs.map(async (bookingDoc) => {
            const booking = { id: bookingDoc.id, ...bookingDoc.data() };
            // Fetch property details for each booking
            const propertyDoc = await getDoc(doc(db, 'properties', booking.propertyId));
            if (propertyDoc.exists()) {
              booking.property = { id: propertyDoc.id, ...propertyDoc.data() };
            }
            return booking;
          })
        );
        setBookings(bookingsList);
      } catch (err) {
        setError('Error fetching user data');
        console.error('Error:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchUserData();
  }, [navigate]);

  const handleLogout = async () => {
    try {
      await auth.signOut();
      navigate('/');
    } catch (err) {
      setError('Error signing out');
      console.error('Error:', err);
    }
  };

  if (loading) {
    return <div className="loading">Loading profile...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="profile-container">
      <div className="profile-header">
        <h1>My Profile</h1>
        <button onClick={handleLogout} className="logout-button">
          Logout
        </button>
      </div>

      <div className="profile-tabs">
        <button
          className={`tab-button ${activeTab === 'profile' ? 'active' : ''}`}
          onClick={() => setActiveTab('profile')}
        >
          Profile Information
        </button>
        <button
          className={`tab-button ${activeTab === 'bookings' ? 'active' : ''}`}
          onClick={() => setActiveTab('bookings')}
        >
          My Bookings
        </button>
      </div>

      <div className="profile-content">
        {activeTab === 'profile' && (
          <div className="profile-info">
            <div className="info-section">
              <h2>Personal Information</h2>
              <div className="info-grid">
                <div className="info-item">
                  <label>Email</label>
                  <p>{user?.email}</p>
                </div>
                <div className="info-item">
                  <label>Name</label>
                  <p>{user?.name || 'Not set'}</p>
                </div>
                <div className="info-item">
                  <label>Phone</label>
                  <p>{user?.phone || 'Not set'}</p>
                </div>
              </div>
            </div>

            <div className="info-section">
              <h2>Account Settings</h2>
              <button className="edit-button">Edit Profile</button>
              <button className="edit-button">Change Password</button>
            </div>
          </div>
        )}

        {activeTab === 'bookings' && (
          <div className="bookings-list">
            <h2>My Bookings</h2>
            {bookings.length === 0 ? (
              <p className="no-bookings">You haven't made any bookings yet.</p>
            ) : (
              bookings.map((booking) => (
                <div key={booking.id} className="booking-card">
                  <div className="booking-image">
                    <img
                      src={booking.property?.images[0]}
                      alt={booking.property?.title}
                    />
                  </div>
                  <div className="booking-details">
                    <h3>{booking.property?.title}</h3>
                    <p className="booking-dates">
                      {new Date(booking.checkIn).toLocaleDateString()} -{' '}
                      {new Date(booking.checkOut).toLocaleDateString()}
                    </p>
                    <p className="booking-status">
                      Status: <span className={booking.status}>{booking.status}</span>
                    </p>
                    <p className="booking-price">
                      Total: ${booking.totalPrice}
                    </p>
                    {booking.status === 'upcoming' && (
                      <button className="cancel-button">Cancel Booking</button>
                    )}
                  </div>
                </div>
              ))
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default Profile;
