      description: "Contemporary apartment in the heart of Mbarara. Close to local attractions and restaurants.",
      host: "Michael Lee",
      hostImage: "https://randomuser.me/api/portraits/men/3.jpg",
      bedrooms: 2,
      bathrooms: 2,
      maxGuests: 4
    },
    {
      id: 6,
      title: "Lake View Villa in Masaka",
      location: "Masaka",
      price: 90,
      image: "https://images.unsplash.com/photo-1600585154340-be6161a56a0c?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80",
      rating: 4.6,
      reviews: 65,
      amenities: ['wifi', 'lake-view', 'pool', 'parking'],
      coordinates: { lat: -0.3333, lng: 31.7333 },
      description: "Spacious villa with stunning views of Lake Victoria. Features a private pool and garden.",
      host: "Lisa Chen",
      hostImage: "https://randomuser.me/api/portraits/women/3.jpg",
      bedrooms: 3,
      bathrooms: 3,
      maxGuests: 6
    },
    {
      id: 7,
      title: "Cozy Bungalow in Hoima",
      location: "Hoima",
      price: 60,
      image: "https://images.unsplash.com/photo-1600607687939-ce8a6c25118c?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80",
      rating: 4.4,
      reviews: 45,
      amenities: ['wifi', 'garden', 'parking'],
      coordinates: { lat: 1.4333, lng: 31.3500 },
      description: "Peaceful bungalow surrounded by nature. Perfect for a quiet getaway in the countryside.",
      host: "Robert Taylor",
      hostImage: "https://randomuser.me/api/portraits/men/4.jpg",
      bedrooms: 1,
      bathrooms: 1,
      maxGuests: 2
    }
  ];

  const areas = ['all', 'Kampala', 'Entebbe', 'Jinja', 'Mbarara', 'Masaka', 'Hoima', 'Fort Portal'];
  const priceRanges = [
    { value: 'all', label: 'All Prices' },
    { value: 'budget', label: 'Under $70', min: 0, max: 70 },
    { value: 'moderate', label: '$70 - $100', min: 70, max: 100 },
    { value: 'luxury', label: 'Over $100', min: 100, max: Infinity }
  ];
  const availableAmenities = [
    { id: 'wifi', label: 'WiFi' },
    { id: 'pool', label: 'Pool' },
    { id: 'parking', label: 'Parking' },
    { id: 'aircon', label: 'Air Conditioning' },
    { id: 'beach', label: 'Beach Access' },
    { id: 'mountain-view', label: 'Mountain View' },
    { id: 'river-view', label: 'River View' },
    { id: 'lake-view', label: 'Lake View' },
    { id: 'garden', label: 'Garden' },
    { id: 'restaurant', label: 'Restaurant' }
  ];

  const sortOptions = [
    { value: 'default', label: 'Recommended' },
    { value: 'price-asc', label: 'Price: Low to High' },
    { value: 'price-desc', label: 'Price: High to Low' },
    { value: 'rating', label: 'Highest Rated' },
    { value: 'reviews', label: 'Most Reviewed' }
  ];

  const handleAmenityChange = (amenityId) => {
    setAmenities(prev => 
      prev.includes(amenityId)
        ? prev.filter(id => id !== amenityId)
        : [...prev, amenityId]
    );
  };

  const filteredApartments = apartments
    .filter(apartment => {
      // Area filter
      const areaMatch = selectedArea === 'all' || apartment.location === selectedArea;
      
      // Price range filter
      const priceRangeObj = priceRanges.find(range => range.value === priceRange);
      const priceMatch = priceRange === 'all' || 
        (apartment.price >= priceRangeObj.min && apartment.price <= priceRangeObj.max);
      
      // Amenities filter
      const amenitiesMatch = amenities.length === 0 || 
        amenities.every(amenity => apartment.amenities.includes(amenity));

      // Search filter
      const searchMatch = searchQuery === '' || 
        apartment.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
        apartment.location.toLowerCase().includes(searchQuery.toLowerCase()) ||
        apartment.description.toLowerCase().includes(searchQuery.toLowerCase());

      return areaMatch && priceMatch && amenitiesMatch && searchMatch;
    })
    .sort((a, b) => {
      switch (sortBy) {
        case 'price-asc':
          return a.price - b.price;
        case 'price-desc':
          return b.price - a.price;
        case 'rating':
          return b.rating - a.rating;
        case 'reviews':
          return b.reviews - a.reviews;
        default:
          return 0;
      }
    });

  return (
    <div className="listings-container">
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search by title, location, or description..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          className="search-input"
        />
        <button 
          className="map-toggle"
          onClick={() => setShowMap(!showMap)}
        >
          {showMap ? 'Hide Map' : 'Show Map'}
        </button>
      </div>

      <div className="filters">
        <div className="filter-section">
          <h2>Filter by Area</h2>
          <select 
            value={selectedArea} 
            onChange={(e) => setSelectedArea(e.target.value)}
            className="area-select"
          >
            {areas.map(area => (
              <option key={area} value={area}>
                {area.charAt(0).toUpperCase() + area.slice(1)}
              </option>
            ))}
          </select>
        </div>

        <div className="filter-section">
          <h2>Price Range</h2>
          <select 
            value={priceRange} 
            onChange={(e) => setPriceRange(e.target.value)}
            className="price-select"
          >
            {priceRanges.map(range => (
              <option key={range.value} value={range.value}>
                {range.label}
              </option>
            ))}
          </select>
        </div>

        <div className="filter-section">
          <h2>Sort By</h2>
          <select 
            value={sortBy} 
            onChange={(e) => setSortBy(e.target.value)}
            className="sort-select"
          >
            {sortOptions.map(option => (
              <option key={option.value} value={option.value}>
                {option.label}
              </option>
            ))}
          </select>
        </div>

        <div className="filter-section">
          <h2>Amenities</h2>
          <div className="amenities-grid">
            {availableAmenities.map(amenity => (
              <label key={amenity.id} className="amenity-checkbox">
                <input
                  type="checkbox"
                  checked={amenities.includes(amenity.id)}
                  onChange={() => handleAmenityChange(amenity.id)}
                />
                {amenity.label}
              </label>
            ))}
          </div>
        </div>
      </div>

      {showMap && (
        <div className="map-container">
          {/* Map component would go here */}
          <p className="map-placeholder">Map View Coming Soon</p>
        </div>
      )}

      <div className="listings-grid">
        {filteredApartments.map(apartment => (
          <div key={apartment.id} className="listing-card">
            <img src={apartment.image} alt={apartment.title} className="listing-image" />
            <div className="listing-details">
              <h3>{apartment.title}</h3>
              <p className="location">{apartment.location}</p>
              <p className="price">${apartment.price} / night</p>
              <div className="rating">
                <span>★ {apartment.rating}</span>
                <span className="reviews">({apartment.reviews} reviews)</span>
              </div>
              <div className="property-details">
                <span>{apartment.bedrooms} beds</span>
                <span>•</span>
                <span>{apartment.bathrooms} baths</span>
                <span>•</span>
                <span>Up to {apartment.maxGuests} guests</span>
              </div>
              <div className="amenities-tags">
                {apartment.amenities.map(amenity => (
                  <span key={amenity} className="amenity-tag">
                    {availableAmenities.find(a => a.id === amenity)?.label}
                  </span>
                ))}
              </div>
              <div className="host-info">
                <img src={apartment.hostImage} alt={apartment.host} className="host-image" />
                <span>Hosted by {apartment.host}</span>
              </div>
              <p className="description">{apartment.description}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Listings;
